function employees_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('employees');
	if(!$arrPerm['insert']) return false;

	$data = [
		'TitleOfCourtesy' => Request::val('TitleOfCourtesy', ''),
		'Photo' => Request::fileUpload('Photo', [
			'maxSize' => 15360000,
			'types' => 'jpg|jpeg|gif|png|webp',
			'noRename' => false,
			'dir' => '',
			'success' => function($name, $selected_id) {
				createThumbnail($name, getThumbnailSpecs('employees', 'Photo', 'tv'));
			},
			'failure' => function($selected_id, $fileRemoved) {
				if(!strlen(Request::val('SelectedID'))) return '';

				/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
				return existing_value('employees', 'Photo', Request::val('SelectedID'));
			},
		]),
		'LastName' => Request::val('LastName', ''),
		'FirstName' => Request::val('FirstName', ''),
		'Title' => Request::val('Title', ''),
		'BirthDate' => Request::dateComponents('BirthDate', ''),
		'Age' => Request::val('Age', ''),
		'HireDate' => Request::dateComponents('HireDate', '1'),
		'Address' => br2nl(Request::val('Address', '')),
		'City' => Request::val('City', ''),
		'Region' => Request::val('Region', ''),
		'PostalCode' => Request::val('PostalCode', ''),
		'Country' => Request::val('Country', ''),
		'HomePhone' => Request::val('HomePhone', ''),
		'Extension' => Request::val('Extension', ''),
		'Notes' => Request::val('Notes', ''),
		'ReportsTo' => Request::lookup('ReportsTo', ''),
	];


	// hook: employees_before_insert
	if(function_exists('employees_before_insert')) {
		$args = [];
		if(!employees_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('employees', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('employees', $recID, calculated_fields()['employees']);

	// hook: employees_after_insert
	if(function_exists('employees_after_insert')) {
		$res = sql("SELECT * FROM `employees` WHERE `EmployeeID`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!employees_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('employees', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) employees_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

function employees_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);

	// launch requests, asynchronously
	curl_batch($requests);
}

function employees_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('employees', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: employees_before_delete
	if(function_exists('employees_before_delete')) {
		$args = [];
		if(!employees_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	// child table: employees
	$res = sql("SELECT `EmployeeID` FROM `employees` WHERE `EmployeeID`='{$selected_id}'", $eo);
	$EmployeeID = db_fetch_row($res);
	$rires = sql("SELECT COUNT(1) FROM `employees` WHERE `ReportsTo`='" . makeSafe($EmployeeID[0]) . "'", $eo);
	$rirow = db_fetch_row($rires);
	if($rirow[0] && !$AllowDeleteOfParents && !$skipChecks) {
		$RetMsg = $Translation["couldn't delete"];
		$RetMsg = str_replace('<RelatedRecords>', $rirow[0], $RetMsg);
		$RetMsg = str_replace('<TableName>', 'employees', $RetMsg);
		return $RetMsg;
	} elseif($rirow[0] && $AllowDeleteOfParents && !$skipChecks) {
		$RetMsg = $Translation['confirm delete'];
		$RetMsg = str_replace('<RelatedRecords>', $rirow[0], $RetMsg);
		$RetMsg = str_replace('<TableName>', 'employees', $RetMsg);
		$RetMsg = str_replace('<Delete>', '<input type="button" class="btn btn-danger" value="' . html_attr($Translation['yes']) . '" onClick="window.location = \'employees_view.php?SelectedID=' . urlencode($selected_id) . '&delete_x=1&confirmed=1&csrf_token=' . urlencode(csrf_token(false, true)) . '\';">', $RetMsg);
		$RetMsg = str_replace('<Cancel>', '<input type="button" class="btn btn-success" value="' . html_attr($Translation[ 'no']) . '" onClick="window.location = \'employees_view.php?SelectedID=' . urlencode($selected_id) . '\';">', $RetMsg);
		return $RetMsg;
	}

	// child table: orders
	$res = sql("SELECT `EmployeeID` FROM `employees` WHERE `EmployeeID`='{$selected_id}'", $eo);
	$EmployeeID = db_fetch_row($res);
	$rires = sql("SELECT COUNT(1) FROM `orders` WHERE `EmployeeID`='" . makeSafe($EmployeeID[0]) . "'", $eo);
	$rirow = db_fetch_row($rires);
	if($rirow[0] && !$AllowDeleteOfParents && !$skipChecks) {
		$RetMsg = $Translation["couldn't delete"];
		$RetMsg = str_replace('<RelatedRecords>', $rirow[0], $RetMsg);
		$RetMsg = str_replace('<TableName>', 'orders', $RetMsg);
		return $RetMsg;
	} elseif($rirow[0] && $AllowDeleteOfParents && !$skipChecks) {
		$RetMsg = $Translation['confirm delete'];
		$RetMsg = str_replace('<RelatedRecords>', $rirow[0], $RetMsg);
		$RetMsg = str_replace('<TableName>', 'orders', $RetMsg);
		$RetMsg = str_replace('<Delete>', '<input type="button" class="btn btn-danger" value="' . html_attr($Translation['yes']) . '" onClick="window.location = \'employees_view.php?SelectedID=' . urlencode($selected_id) . '&delete_x=1&confirmed=1&csrf_token=' . urlencode(csrf_token(false, true)) . '\';">', $RetMsg);
		$RetMsg = str_replace('<Cancel>', '<input type="button" class="btn btn-success" value="' . html_attr($Translation[ 'no']) . '" onClick="window.location = \'employees_view.php?SelectedID=' . urlencode($selected_id) . '\';">', $RetMsg);
		return $RetMsg;
	}

	// delete file stored in the 'Photo' field
	$res = sql("SELECT `Photo` FROM `employees` WHERE `EmployeeID`='{$selected_id}'", $eo);
	if($row = @db_fetch_row($res)) {
		if($row[0] != '') {
			@unlink(getUploadDir('') . $row[0]);
			$thumbDV = preg_replace('/\.(jpg|jpeg|gif|png|webp)$/i', '_dv.$1', $row[0]);
			$thumbTV = preg_replace('/\.(jpg|jpeg|gif|png|webp)$/i', '_tv.$1', $row[0]);
			@unlink(getUploadDir('') . $thumbTV);
			@unlink(getUploadDir('') . $thumbDV);
		}
	}

	sql("DELETE FROM `employees` WHERE `EmployeeID`='{$selected_id}'", $eo);

	// hook: employees_after_delete
	if(function_exists('employees_after_delete')) {
		$args = [];
		employees_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='employees' AND `pkValue`='{$selected_id}'", $eo);
}

function employees_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('employees', $selected_id, 'edit')) return false;

	$data = [
		'TitleOfCourtesy' => Request::val('TitleOfCourtesy', ''),
		'Photo' => Request::fileUpload('Photo', [
			'maxSize' => 15360000,
			'types' => 'jpg|jpeg|gif|png|webp',
			'noRename' => false,
			'dir' => '',
			'id' => $selected_id,
			'success' => function($name, $selected_id) {
				createThumbnail($name, getThumbnailSpecs('employees', 'Photo', 'tv'));
			},
			'removeOnSuccess' => true,
			'removeOnRequest' => true,
			'remove' => function($selected_id) {
				// delete old file from server
				$oldFile = existing_value('employees', 'Photo', $selected_id);
				if(!$oldFile) return;

				@unlink(getUploadDir('') . $oldFile);

				// delete thumbnails
				preg_match('/^[a-z0-9_]+\.(jpg|jpeg|gif|png|webp)$/i', $oldFile, $m);
				$thumbDV = str_replace(".{$m[1]}ffffgggg", "_dv.{$m[1]}", $oldFile . 'ffffgggg');
				$thumbTV = str_replace(".{$m[1]}ffffgggg", "_tv.{$m[1]}", $oldFile . 'ffffgggg');
				@unlink(getUploadDir('') . $thumbTV);
				@unlink(getUploadDir('') . $thumbDV);
			},
			'failure' => function($selected_id, $fileRemoved) {
				if($fileRemoved) return '';
				return existing_value('employees', 'Photo', $selected_id);
			},
		]),
		'LastName' => Request::val('LastName', ''),
		'FirstName' => Request::val('FirstName', ''),
		'Title' => Request::val('Title', ''),
		'BirthDate' => Request::dateComponents('BirthDate', ''),
		'Age' => Request::val('Age', ''),
		'HireDate' => Request::dateComponents('HireDate', ''),
		'Address' => br2nl(Request::val('Address', '')),
		'City' => Request::val('City', ''),
		'Region' => Request::val('Region', ''),
		'PostalCode' => Request::val('PostalCode', ''),
		'Country' => Request::val('Country', ''),
		'HomePhone' => Request::val('HomePhone', ''),
		'Extension' => Request::val('Extension', ''),
		'Notes' => Request::val('Notes', ''),
		'ReportsTo' => Request::lookup('ReportsTo', ''),
	];

	// get existing values
	$old_data = getRecord('employees', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: employees_before_update
	if(function_exists('employees_before_update')) {
		$args = ['old_data' => $old_data];
		if(!employees_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'employees', 
		backtick_keys_once($set), 
		['`EmployeeID`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="employees_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('employees', $data['selectedID'], calculated_fields()['employees']);

	// hook: employees_after_update
	if(function_exists('employees_after_update')) {
		$res = sql("SELECT * FROM `employees` WHERE `EmployeeID`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['EmployeeID'];
		$args = ['old_data' => $old_data];
		if(!employees_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='employees' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

